mapper.insert:
insert
  into member
  (
    user_id
  , user_pwd
  , user_name
  , email
  , gender
  , age
  , phone
  , address
  )
  values
  (
    #{userId}
  , #{userPwd}
  , #{userName}
  , #{email}
  , #{gender}
  , #{age}
  , #{phone}
  , #{address}
  )

mapper.update:
update
       member
   set email = #{email}
     , gender = #{gender}
     , age = #{age}
     , phone = #{phone}
     , address = #{address}
     , user_name = #{userName}
     , modify_date = sysdate
 where user_id = #{userId}
