mapper.update:
update
        member
   set  member_phone = #{memberPhone}
      , member_profile = #{memberProfile}
 where member_id = #{memberId}

mapper.delete:
delete 
 	from follow
   where member_no = #{memberNo}
     and follow_member_no = #{followMemberNo}

mapper.insert:
insert
  into follow
  (
    member_no
  , follow_member_no
  )
  values
  (
    #{memberNo}
  , #{followMemberNo}

  )

mapper.select:
select 
       count(*)
  from follow
 where member_no = #{followMemberNo}
   and follow_member_no = #{memberNo}
